/** 
 *              Callback Functions           
 */
function callback_one($response, $info, $request) {
	global $regexpPrices;
	global $regexpPrices2;
	global $regexpRegion;
	global $matches;
	global $itemsArray;
	global $bad_urls;
	global $time_start;

echo $response;die();

	AngryCurl::add_debug_msg(PHP_EOL.$request->url);
	AngryCurl::add_debug_msg($info['http_code']);
	AngryCurl::add_debug_msg($request->options[10004]);

  if ($info['http_code'] !== 200 /*&& $info['http_code'] !== 500 && $info['http_code'] != 404*/) {
    $bad_urls[] = $request->url;
  	/**
  	 * Выходим, если прошло слишком много времени
  	 */  	
		if (round(microtime(1) - $time_start, 0) >= 180) { 
			$bad_urls = array();
		}      
  } else {
  		preg_match($regexpRegion, $response, $mregion);
  		AngryCurl::add_debug_msg($mregion[1]);

	  	preg_match_all($regexpPrices, $response, $matches2, PREG_SET_ORDER); // 	
	  	//print_r($matches2);
	  	foreach ($matches2 as $key) {
				preg_match($regexpPrices2, $key[1], $matches);
				//print_r($matches);
				if ($matches[1]) {
					if (strripos($key[2], 'class="btn btn-cart"') !== false) {
						price_change_detect('http://' . ENGINE_CURR . trim($matches[1]), trim($matches[2]), preg_replace('~[\D]+~', '' , $matches[3]), date("d.m.y-H:i:s"), $request->options[10004], $request->options[10006], $request->options[10018], ENGINE_CURR, ENGINE_TYPE);
						$itemsArray['http://' . ENGINE_CURR . trim($matches[1])] = array(trim($matches[2]), preg_replace('~[\D]+~', '' , $matches[3]), date("d.m.y-H:i:s"), $request->options[10004]); //iconv('UTF-8', 'Windows-1251', 
						AngryCurl::add_debug_msg(trim($matches[2]).' | '.preg_replace('~[\D]+~', '' , $matches[3]));
					} else {
						price_change_detect('http://' . ENGINE_CURR . trim($matches[1]), trim($matches[2]), '0', date("d.m.y-H:i:s"), $request->options[10004], $request->options[10006], $request->options[10018], ENGINE_CURR, ENGINE_TYPE);
						$itemsArray['http://' . ENGINE_CURR . trim($matches[1])] = array(trim($matches[2]), '0', date("d.m.y-H:i:s"), $request->options[10004]); //iconv('UTF-8', 'Windows-1251', 
						AngryCurl::add_debug_msg(trim($matches[2]).' | 0');
					}				
				}
			} 
		//}	
  }
}
